using System;
using System.Globalization;
using System.IO;
using System.Windows.Controls;
using System.Windows.Data;
using System.Windows.Media.Imaging;

namespace ClientWPF.Converter
{

    internal class ConverterBase64ToImage : IValueConverter
    {
        public object Convert(object value, Type targetType, object parameter, CultureInfo culture)
        {
            try
            {
                var binaryData =
                    System.Convert.FromBase64String(string.IsNullOrEmpty((string)value)
                        ? GetDefaultPic()
                        : (string)value);

                var bi = new BitmapImage();
                bi.BeginInit();
                bi.CacheOption = BitmapCacheOption.OnLoad;
                bi.StreamSource = new MemoryStream(binaryData);
                bi.EndInit();
                return bi;
            }
            catch (Exception)
            {
                return null;
            }
        }

        public object ConvertBack(object value, Type targetType, object parameter, CultureInfo culture)
        {
            using (var ms = new MemoryStream())
            {
                // Convert Image to byte[]
                var tmp = ((Image)value).Source as BitmapImage;

                BitmapEncoder enc = new BmpBitmapEncoder();
                if (tmp != null) enc.Frames.Add(BitmapFrame.Create(tmp));
                enc.Save(ms);
                return System.Convert.ToBase64String(ms.ToArray());
            }
        }

        private string GetDefaultPic()
        {
            return
                "";
        }
    }
}
